mapper.select:
SELECT COUNT(*)
  FROM PRODUCT_T

 WHERE CATEGORY_NO = #{categoryNo}

SELECT A.PRODUCT_NO, A.PRODUCT_NAME, A.PRODUCT_TITLE, A.PRODUCT_PRICE, A.PRODUCT_CREATED_AT, A.PRODUCT_DESCRIPTION, A.PRODUCT_SIZE, A.PRODUCT_WARNING, A.CATEGORY_NO, A.PRODUCT_SALES, A.PRODUCT_HIT, A.PRODUCT_RATING, A.PRODUCT_COUNT, A.REVIEW_COUNT
  FROM (SELECT ROW_NUMBER() OVER(ORDER BY ${order} ${ascDesc}) AS RN, P.PRODUCT_NO, P.PRODUCT_NAME, P.PRODUCT_TITLE, P.PRODUCT_PRICE, P.PRODUCT_CREATED_AT, P.PRODUCT_DESCRIPTION, P.PRODUCT_SIZE, P.PRODUCT_WARNING, P.CATEGORY_NO, P.PRODUCT_SALES, P.PRODUCT_HIT, P.PRODUCT_RATING
             , (SELECT SUM(OPTION_COUNT) FROM PRODUCT_OPTION_T OPT WHERE P.PRODUCT_NO = OPT.PRODUCT_NO) AS PRODUCT_COUNT
             , (SELECT COUNT(*) FROM REVIEW_T R WHERE R.OPTION_NO IN (SELECT OPT.OPTION_NO FROM PRODUCT_OPTION_T OPT WHERE P.PRODUCT_NO = OPT.PRODUCT_NO)) AS REVIEW_COUNT
          FROM PRODUCT_T P

           WHERE CATEGORY_NO = #{categoryNo}

        ) A
 WHERE A.RN BETWEEN #{begin} AND #{end}

SELECT OPTION_NO, PRODUCT_NO, OPTION_NAME, ADD_PRICE, OPTION_COUNT
  FROM PRODUCT_OPTION_T
 WHERE PRODUCT_NO = #{productNo}

mapper.insert:
INSERT INTO PRODUCT_T (
    PRODUCT_NO
  , PRODUCT_NAME
  , PRODUCT_TITLE
  , PRODUCT_PRICE
  , PRODUCT_CREATED_AT
  , PRODUCT_DESCRIPTION
  , PRODUCT_SIZE
  , PRODUCT_WARNING
  , CATEGORY_NO
  , PRODUCT_HIT
  , PRODUCT_SALES
  , PRODUCT_RATING
  ) VALUES (
    PRODUCT_SEQ.NEXTVAL
  , #{productName}
  , #{productTitle}
  , #{productPrice}
  , TO_CHAR(SYSDATE, 'YYYY/MM/DD HH24:MI:SS')
  , #{productDescription}
  , #{productSize}
  , #{productWarning}
  , #{categoryNo}
  , 0
  , 0
  , null
)
