mapper.insert:

    SELECT QATTACH_SEQ.NEXTVAL AS FROM DUAL

INSERT INTO QNA_T (
    QNA_NO
    , TITLE
    , CONTENTS
    , USER_NO
    , CREATED_AT
    , TEXT_PW
    , PRODUCT_NO
    , STATUS
    , DEPTH
    , GROUP_NO
    , CHECK_FLAG
) VALUES (
    #{qnaNo}
    , #{title}
    , #{contents}
    , #{userNo}
    , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
    , #{textPw}
    , #{productNo}
    , 1
    , 0
    , 0
    , #{checkFlag}
)

INSERT INTO QATTACH_T (
    QATTACH_NO
    , QNA_NO
    , PATH
    , ORIGINAL_FILENAME
    , FILESYSTEM_NAME
) VALUES (
     QATTACH_SEQ.NEXTVAL
    , #{qnaNo}
    , #{path}
    , #{originalFilename}
    , #{filesystemName}
)
