mapper.select:
SELECT USER_NO USER_NO,EMAIL,PW,NAME,GENDER,MOBILE,POSTCODE,ROAD_ADDRESS,JIBUN_ADDRESS,DETAIL_ADDRESS,AGREE,JOIN_STATE,PW_MODIFIED_AT,JOINED_AT,ADMIN_AUTHOR_STATE,POINT
FROM USER_T 

     EMAIL = #{email}
     AND PW = #{pw}
     AND USER_NO = #{userNo}

mapper.insert:
INSERT INTO ACCESS_USER_T (
    EMAIL
  , LOGIN_AT
) VALUES (
    #{email}
  , TO_CHAR(SYSDATE,'YYYY-MM-DD_HH24:MI:SS')
)

mapper.select:
SELECT USER_NO USER_NO,EMAIL,PW,NAME,GENDER,MOBILE,POSTCODE,ROAD_ADDRESS,JIBUN_ADDRESS,DETAIL_ADDRESS,AGREE,JOIN_STATE,PW_MODIFIED_AT,JOINED_AT,INACTIVED_AT,ADMIN_AUTHOR_STATE,POINT
FROM INACTIVE_USER_T

EMAIL = #{email}
AND PW = #{pw}

SELECT EMAIL, JOINED_AT, LEAVED_AT
  FROM LEAVE_USER_T
 WHERE EMAIL = #{email}

SELECT POINT 
FROM USER_T
WHERE USER_NO = #{userNo}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , EMAIL
  , PW
  , NAME
  , GENDER
  , MOBILE
  , POSTCODE
  , ROAD_ADDRESS
  , JIBUN_ADDRESS
  , DETAIL_ADDRESS
  , AGREE
  , JOIN_STATE
  , PW_MODIFIED_AT
  , JOINED_AT
  ,ADMIN_AUTHOR_STATE
  ,POINT
) VALUES (
    USER_SEQ.NEXTVAL
  , #{email}
  , #{pw}
  , #{name}
  , #{gender}
  , #{mobile}
  , #{postcode}
  , #{roadAddress}
  , #{jibunAddress}
  , #{detailAddress}
  , #{agree}
  , 0
  , TO_CHAR(SYSDATE,'YYYY-MM-DD_HH24:MI:SS')
  , TO_CHAR(SYSDATE,'YYYY-MM-DD_HH24:MI:SS')
  ,#{adminAuthorState}
  ,0
)

mapper.update:
UPDATE USER_T
   SET NAME = #{name}
     , GENDER = #{gender}
     , MOBILE = #{mobile}
     , POSTCODE = #{postcode}
     , ROAD_ADDRESS = #{roadAddress}
     , JIBUN_ADDRESS = #{jibunAddress}
     , DETAIL_ADDRESS = #{detailAddress}
     , AGREE = #{agree}
 WHERE USER_NO = #{userNo}

mapper.insert:
INSERT INTO LEAVE_USER_T (
    EMAIL
  , JOINED_AT
  , LEAVED_AT
) VALUES (
    #{email}
  , #{joinedAt}
  , TO_CHAR(SYSDATE,'YYYY-MM-DD_HH24:MI:SS')
)
